#!/usr/bin/env python#title           :startEC2Instances.py#description     :AWS Lambda python script to start instances in all regions with "LambdaStart" tag set to "true"#author          :booysenn#date            :20160915#version         :0.1#usage           :copy to Lambda function. Can trigger with Cloudwatch schedule#notes           :Execution time 13 seconds#==============================================================================import boto3def lambda_handler(event, context):    ec2 = boto3.client('ec2')    regions = ec2.describe_regions()    for region in regions['Regions']:        ec2 = boto3.client('ec2', region_name=region['RegionName'])        response = ec2.describe_instances( Filters=[{'Name':'tag:LambdaStart', 'Values':['true']}])        instances = []        for res in response['Reservations']:            for inst in res['Instances']:                instances.append(inst['InstanceId'])                ec2.start_instances(InstanceIds=instances)